insert into test.card_rules (game, rules) values ( 'simple addition ==>elevens', 'Cards are valued Ace=1 through Ten=10. Discard face cards in trios with one each jack, queen, king. Discard other cards in pairs with a total value of 11. Discarded cards are replaced from the deck until it empties. Win by discarding everything.');
insert into test.card_rules (game, rules) values ( 'simple addition ==>thirteens', 'Cards are valued Ace=1 through King=13. Discard cards in pairs with a total value of 13. Discarded cards are replaced from the deck until it empties. Win by discarding everything.');
insert into test.card_rules (game, rules) values ( 'simple addition ==>tens', 'Cards are valued Ace=1 thought Nine=9. Discards tens and face cards in quartets of the same suit. Discard other cards in pairs with a total value of 10. Discarded cards are replaced from the deck until it empties. Win by discarding everything.');
insert into test.card_rules (game, rules) values ( 'simple addition ==>fifteens', 'Cards are valued Ace=1 through Nine=9. Discard tens and face cards in quartets of the same suit. Discard other cards in groups of any size with a total value of 15. Win by emptying the draw deck.');
insert into test.card_rules (game, rules) values ( 'block solitaire ==>elevens', 'Cards are valued Ace=1 through Ten = 10.  Cover pairs of numbers totaling eleven from the deck. Face cards cannot be covered. Win by emptying the draw deck. Note: Face cards dealt during setup were moved to the bottom of the deck.');
insert into test.card_rules (game, rules) values ( 'block solitaire ==>tens', 'Cards are valued Ace=1 through Nine=9. Any two face cards can be covered as a pair. Other cards are covered in pairs with a total value of 10. Tens block futher play on that pile. Win by emptying the draw deck.');
insert into test.card_rules (game, rules) values ( 'baroness', 'Cards are valued Ace=1 through King=13. Discard Kings singly and other card in pairs with a total value of 13 from the tops of piles. When there are no further plays, deal atop the five piles (including empty piles). When the deck size is two, reveal the last two cards in separate piles. Win by discarding everything.');
insert into test.card_rules (game, rules) values ( 'fourteen puzzle', 'Cards are valued Ace=1 through King=13.  Discard cards on the top of piles in pairs with a total value of 14. Win by discarding everything.');
insert into test.card_rules (game, rules) values ( 'pyramid', 'Cards are valued Ace=1 through King=13. Discard available kings singly and other available cards in pairs totaling 13. Cards in the pyramid are available if no other card covers them. The top card of the play pile is available. The top card of the draw becomes available when drawn. If the drawn card is not discarded it must be placed on the play pile.  The play pile may be returned as the draw deck without shuffling up to three times when the draw deck is empty. Win by discarding all 52 cards.');
insert into test.card_rules (game, rules) values ( 'nestor', 'Discard uncovered cards in pairs of the same rank. You may reveal a card from the draw deck, but must then immediately select an uncovered card to pair it with or lose the card. Win by emptying the tableau.');
insert into test.card_rules (game, rules) values ( 'monte carlo', 'Discard adjacent cards of the same rank in pairs.  Cards will shift left and to the next higher row to fill holes prior to refilling the grid. Discard all 52 cards to win.');